// This is the corrected script to parse Notion blocks into a structured JSON object.
// VERSION 3: Adds extraction of the parent page_id.

const structuredData = {};
let currentKey = null;
let pageId = null; // NEW: Variable to store the parent page ID.

// A map to convert Notion heading text to clean JSON keys.
const keyMap = {
  'Target Audience': 'target_audience',
  'Tone of Voice': 'tone_of_voice',
  'SEO Keywords': 'seo_keywords',
  'Key Talking Points': 'key_talking_points',
  'Sources & Links to Include': 'sources_and_links',
  'Call to Action (CTA)': 'call_to_action',
  'Excluded Topics': 'excluded_topics'
};

// A set of placeholder texts to ignore, so they don't get added to your data.
const ignoreContent = new Set([
  'Use bullet points to list the main ideas, arguments, or steps the article must cover. This will become the outline for the AI-generated draft.',
  'Paste any URLs for research, sources, or internal links that should be referenced or included in the article.',
  'What is the single most important action you want the reader to take after finishing the article? E.g., "Sign up for our webinar," "Try this workflow template," "Leave a comment with your experience."',
  'List any specific topics, competitors, or phrases to avoid. This helps keep the AI on track. E.g., "Do not mention competitor X," "Avoid discussing pricing details," "Don\'t use overly complex jargon."'
]);


for (const item of items) {
  const block = item.json;

  // NEW: Grab the page_id from the first block that has one.
  // This runs only once.
  if (!pageId && block.parent && block.parent.page_id) {
    pageId = block.parent.page_id;
  }

  // Ensure we have a block with content and type before proceeding
  if (!block || !block.type || typeof block.content !== 'string') {
    continue;
  }

  const blockType = block.type;
  const blockContent = block.content.trim();

  // If the content is in our ignore list, skip it.
  if (ignoreContent.has(blockContent)) {
    continue;
  }

  // Check if the current block is a heading that we care about.
  if (blockType === 'heading_2' && keyMap[blockContent]) {
    currentKey = keyMap[blockContent]; // Update our current section key
    // Initialize the key in our result object. Lists get an array, others get a string.
    if (['seo_keywords', 'key_talking_points', 'sources_and_links', 'excluded_topics'].includes(currentKey)) {
      structuredData[currentKey] = [];
    } else {
      structuredData[currentKey] = '';
    }
    continue; // Move to the next block
  }

  // If we are inside a section (currentKey is set), process the content.
  if (currentKey) {
    if (blockType === 'bulleted_list_item' || blockType === 'numbered_list_item') {
      if (Array.isArray(structuredData[currentKey])) {
        structuredData[currentKey].push(blockContent);
      }
    } else if (blockType === 'paragraph') {
      structuredData[currentKey] += (structuredData[currentKey] ? ' ' : '') + blockContent;
    }
  }
}

// NEW: Add the captured page_id to our final data object.
if (pageId) {
  structuredData.page_id = pageId;
}

// Return the single, structured JSON object for the next node.
return [
  {
    json: structuredData
  }
];

// Get the title from the previous node
const title = $input.first().json.output.blog_title;

// Replace spaces with underscores and add .md
const filename = title.replace(/\s+/g, '_') + '.md';

// Return the result as output
return {
  filename
};

